public class BooleanTest {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		int age = 18;

		if (age > 17) {
			System.out.println("Grab your beer. ");
		} else {
			System.out.println("Go out!");
		}

		String name = "Anna";

		if (name == "Anna") {
			System.out.println("Yo! " + name);
		}

		if (name.equals("Anna")) {
			System.out.println("Yo! " + name);
		}
	}

}
